-- author: CodeBaker & Leonard1on
-- desc:   Game Off 2020 Entry
-- script: lua
function init()
    ticks = 0
    p = {
        image = 256,
        x = 96,
        y = 24,
        vx = 0,
        vy = 0,
        flip = 0,
        moveY = function(self)
            self.y = self.y + self.vy;
        end,
        moveX = function(self)
            self.x = self.x + self.vx;
        end
    }
    solidTiles = {
        [0] = false,
        [1] = true,
        [2] = true
    }
end

function isSolid(x, y)
    return solidTiles[mget((x) // 8, (y) // 8)]
end

function writeSpeech(text)
    final = ''
    for i = 1, #text do
        final = final .. string.sub(text, i, i)
        if i % 38 == 0 then
            final = final .. '\n'
        end
    end
    rect(0, (136 * 0.75) // 1, 240, 136 * 0.25, 8)
    rectb(0, (136 * 0.75) // 1, 240, 136 * 0.25, 4)
    print(final, 5, (136 * 0.75) + 5 // 1, 12, true)
    seconds = (ticks // 60)
    if seconds % 2 == 0 then
        tri(235, 129, 229, 129, 232, 132, 12)
    end
end

init()
function TIC()
    input()
    p:moveX()
    p:moveY()
    cls(12)
    map(0, 0, 30, 17)
    spr(258, 29 * 8, 5 * 8, 15)
    spr(p.image, p.x, p.y, 6, 1, p.flip)
    writeSpeech("The quick brown fox jumps over the lazy dog.")
    p.image = 256
    ticks = ticks + 1
end

function input()
    if btn(2) then
								sideMotion()
        p.vx = -1
        p.flip = 1

    elseif btn(3) then
								sideMotion()
								p.vx = 1
        p.flip = 0
    else
        p.vx = 0
    end

    if btn(1) and not btn(2) and not btn(3) then
        p.image = 272
    end

    if isSolid(p.x + p.vx, p.y + p.vy) or isSolid(p.x + 7 + p.vx, p.y + p.vy) or isSolid(p.x + p.vx, p.y + 7 + p.vy) or
        isSolid(p.x + 7 + p.vx, p.y + 7 + p.vy) then
        p.vx = 0
    end

    if isSolid(p.x, p.y + 8 + p.vy) or isSolid(p.x + 7, p.y + 8 + p.vy) then
        p.vy = 0
    else
        p.vy = p.vy + 0.2
    end

    if p.vy == 0 and btnp(4) then
        p.vy = -2.5
    end

    if p.vy < 0 and (isSolid(p.x + p.vx, p.y + p.vy) or isSolid(p.x + 7 + p.vx, p.y + p.vy)) then
        p.vy = 0
    end
end

function sideMotion()
				t = 1+ticks%60//30
				if(t==1) then
								p.image = 257
				else
							 p.image = 258
    end
end

